import Header from "./Header/Header";
import Main from "./Main/Main";
import Footer from "./Footer/Footer";
import PopupWithForm from "./PopapWithForm/PopupWithForm";
import PopupImg from "./PopupImg/PopupImg";
import { useState } from "react";


function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function closeAllPopup(){
    setIsEditAvatarPopupOpen(false)
    setIsEditProfilePopupOpen(false)
    setIsAddPlacePopupOpen(false)
  }


  return (
    <div className="page__content">
      <Header />

      <Main 
      oneEditProfile={handleEditProfileClick}
      onEditAvatar={handleEditAvatarClick}
      onAddPlace={handleAddPlaceClick}
       />

      <Footer />

      <PopupWithForm
        name="profile-edit"
        title="Редактировать профиль"
        btnTitle="Сохранить"
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopup}
      >
        {" "}
        <fieldset className="popup__elements">
          <input
            className="popup__element"
            type="text"
            name="profilename"
            id="name"
            placeholder="Имя"
            minLength={2}
            maxLength={40}
            required=""
          />
          <span className="popup__error popup__error_type_profilename" />
          <input
            className="popup__element"
            type="text"
            name="job"
            id="job"
            placeholder="Вид деятельности"
            minLength={2}
            maxLength={200}
            required=""
          />
          <span className="popup__error popup__error_type_job" />
        </fieldset>
      </PopupWithForm>

      <PopupWithForm
        name="avatar-edit"
        title="Обновить аватар"
        btnTitle="Обновить"
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopup}
      >
        <fieldset className="popup__elements">
          <input
            className="popup__element"
            type="url"
            name="avatar"
            id="ava"
            placeholder="Ссылка на картинку"
            required=""
          />
          <span className="popup__error popup__error_type_avatar" />
        </fieldset>
      </PopupWithForm>

      <PopupWithForm
        name="add-Card"
        title="Новое место"
        btnTitle="Добавить"
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopup}
      >
        <fieldset className="popup__elements">
          <input
            className="popup__element"
            type="text"
            name="title"
            id="title"
            placeholder="Название"
            minLength={2}
            maxLength={30}
            required=""
          />
          <span className="popup__error popup__error_type_title" />
          <input
            className="popup__element"
            type="url"
            name="link"
            id="link"
            placeholder="Ссылка на картинку"
            required=""
          />
          <span className="popup__error popup__error_type_link" />
        </fieldset>
      </PopupWithForm>

      <PopupWithForm name="delete" title="Вы уверены?" btnTitle="Да" />
      <PopupImg />
    </div>
  );
}

export default App;
