import { useContext } from "react";
import currentUserContext from "../../contexts/CurrentUserContext";

export default function Card({ card, onClickCard, onDelete, onCardLike}) {

  const currentUser = useContext(currentUserContext)
  const isLiked = card.likes.some((i) => i._id === currentUser._id);

  return (
    <div className="element__card">
      {currentUser._id === card.owner._id &&   <button className="element__trash-btn" type="button" onClick={onDelete} />}


      <img
        className="element__foto"
        alt={card.name}
        src={card.link}
        onClick={() => onClickCard({ link: card.link, name: card.name })}
      />
      <div className="element__names">
        <h2 className="element__name">{card.name}</h2>
        <div className="element__like-container">
          <button
            className={`element__like-btn ${isLiked ? 'element__like-btn_active' : ''}` }
            type="button"
            title="Оценить фотографию"
            aria-label="Поставить лайк"
            onClick={() => onCardLike(card)}
            likes ={card.likes} myid = {currentUser._id} cardid = {card._id}
          />
          <span className="element__like-counter">{card.likes.length}</span>
        </div>
      </div>
    </div>
  );
}
