import Header from "./Header/Header";
import Main from "./Main/Main";
import Footer from "./Footer/Footer";
import PopupWithForm from "./PopapWithForm/PopupWithForm";
import ImagePopup from "./ImagePopup/ImagePopup";
import { useCallback, useState, useEffect} from "react";
import currentUserContext from "../contexts/CurrentUserContext";
import api from "../utils/api";

function App() {
  // Popup
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isImagePopupOpen, setIsImagePopup] = useState(false);
  const [selectedCard, setSelectedCard] = useState({});
  const [isDelPopupOpen, setDelPopupOpen] = useState(false);
// Context
const [currentUser, setCurrentUser] = useState({})
// Card
const [cards, setCards] = useState([]);
// like
const [isLike, setIsLike] = useState

  const setCloseAllPopup = useCallback(() => {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsImagePopup(false);
    setDelPopupOpen(false);
  }, []);
  const closePopupByEsc = useCallback(
    (event) => {
      if (event.key === "Escape") setCloseAllPopup();
      document.removeEventListener("keydown", closePopupByEsc);
    },
    [setCloseAllPopup]
  );
  const CloseAllPopup = useCallback(() => {
    setCloseAllPopup();
    document.addEventListener("keydown", closePopupByEsc);
  }, [setCloseAllPopup, closePopupByEsc])

  function setEventListenerByEsc() {
    document.addEventListener("keydown", closePopupByEsc);

  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
    setEventListenerByEsc();
  }
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
    setEventListenerByEsc();
  }
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
    setEventListenerByEsc();
  }
  function handleCardClick(card) {
    setSelectedCard(card);
    setIsImagePopup(true);
    setEventListenerByEsc();
  }
  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.setUserInfo(card._id, !isLiked).then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
}
  setEventListenerByEsc();
  function handleDelPopupClick() {
    setDelPopupOpen(true);
    setEventListenerByEsc();
  }

  useEffect(() => {
    Promise.all([api.getInfo(), api.getCards()])

      .then(([resInfo, resCards]) => {
        setCurrentUser(resInfo)
        setCards(resCards);
      })
      .catch((error) => console.error(`Ошибка  ${error}`));
  }, []);


  return (

    <currentUserContext.Provider value={currentUser}>
    <div className="page__content">
      <Header />

      <Main
        oneEditProfile={handleEditProfileClick}
        onEditAvatar={handleEditAvatarClick}
        onAddPlace={handleAddPlaceClick}
        onClickCard={handleCardClick}
        onDelete={handleDelPopupClick}
        cards={cards}
        onCardLike={handleCardLike}
      />

      <Footer />

      <PopupWithForm
        name="profile-edit"
        title="Редактировать профиль"
        btnTitle="Сохранить"
        isOpen={isEditProfilePopupOpen}
        onClose={CloseAllPopup}
      >
        {" "}
        <fieldset className="popup__elements">
          <input
            className="popup__element"
            type="text"
            name="profilename"
            id="name"
            placeholder="Имя"
            minLength={2}
            maxLength={40}
            required=""
          />
          <span className="popup__error popup__error_type_profilename" />
          <input
            className="popup__element"
            type="text"
            name="job"
            id="job"
            placeholder="Вид деятельности"
            minLength={2}
            maxLength={200}
            required=""
          />
          <span className="popup__error popup__error_type_job" />
        </fieldset>
      </PopupWithForm>

      <PopupWithForm
        name="avatar-edit"
        title="Обновить аватар"
        btnTitle="Обновить"
        isOpen={isEditAvatarPopupOpen}
        onClose={CloseAllPopup}
      >
        <fieldset className="popup__elements">
          <input
            className="popup__element"
            type="url"
            name="avatar"
            id="ava"
            placeholder="Ссылка на картинку"
            required=""
          />
          <span className="popup__error popup__error_type_avatar" />
        </fieldset>
      </PopupWithForm>

      <PopupWithForm
        name="add-Card"
        title="Новое место"
        btnTitle="Добавить"
        isOpen={isAddPlacePopupOpen}
        onClose={CloseAllPopup}
      >
        <fieldset className="popup__elements">
          <input
            className="popup__element"
            type="text"
            name="title"
            id="title"
            placeholder="Название"
            minLength={2}
            maxLength={30}
            required=""
          />
          <span className="popup__error popup__error_type_title" />
          <input
            className="popup__element"
            type="url"
            name="link"
            id="link"
            placeholder="Ссылка на картинку"
            required=""
          />
          <span className="popup__error popup__error_type_link" />
        </fieldset>
      </PopupWithForm>

      <PopupWithForm
        name="delete"
        title="Вы уверены?"
        btnTitle="Да"
        isOpen={isDelPopupOpen}
        onClose={CloseAllPopup}
      />
      <ImagePopup
        card={selectedCard}
        isOpen={isImagePopupOpen}
        onClose={CloseAllPopup}
      />
    </div>
    </currentUserContext.Provider>
  );
}

export default App;
