import { useEffect, useState } from "react";
import api from "../../utils/api";
import Card from "../Card/Card";

export default function Main({
  oneEditProfile,
  onEditAvatar,
  onAddPlace,
  onClickCard,
}) {
  const [userName, setUserName] = useState("");
  const [userDescription, setUserDescription] = useState("");
  const [userAvatar, setUserAvatar] = useState("");
  const [cards, setCards] = useState([]);

  useEffect(() => {
    Promise.all([api.getInfo(), api.getCards()])

      .then(([resInfo, resCards]) => {
        setUserName(resInfo.name);
        setUserDescription(resInfo.about);
        setUserAvatar(resInfo.avatar);
        setCards(resCards);
        console.log(getInfo)
      })
      // .catch((error) => console.error(`Ошибка  ${error}`))
  }, []);

  return (
    <main className="content">
      <section className="profile page__profile">
        <div className="profile__ava-container">
          <img
            className="profile__img"
            title="изображение профиля"
            alt="изображение профиля"
            src={userAvatar}
          />
          <button
            type="button"
            className="profile__button-ava"
            onClick={onEditAvatar}
          />
        </div>
        <div className="profile__info">
          <h1 className="profile__title">{userName}</h1>
          <button
            type="button"
            className="profile__button-edit"
            onClick={oneEditProfile}
          />
          <p className="profile__subtitle">{userDescription}</p>
        </div>
        <button
          type="button"
          className="profile__button-add"
          onClick={onAddPlace}
        />
      </section>
      <section className="element">
        {cards.map((card) => (
          <Card key={card._id} card={card} onClickCard={onClickCard} />
        ))}
      </section>
    </main>
  );
}
