export default function PopupWithForm({
  name,
  title,
  btnTitle,
  children,
  isOpen,
  onClose,
  onSubmit,
  isValid
}) {
  return (
    <div className={`popup popup_type_${name}${isOpen && " popup_opened"}`} onClick={onClose}>
      <div className={`popup__container ${name === 'delete' && 'popup__container_del'}`} onClick={(event)=> event.stopPropagation()}>
        <h2 className={`popup__title`}>{title}</h2>
        <button
          className="popup__close"
          type="button"
          title="Закрыть окно"
          aria-label="Закрыть"
          onClick={onClose}
        />
        <form
          className="popup__form popup__form-edit"
          name={name}
          action="#"
          method="post" 
          onSubmit={onSubmit}
          noValidate
        >
          {children}
          <button className={`popup__btn ${isValid ?  '' : "popup__btn_disabled"}`} type="submit">
            {btnTitle}
          </button>
        </form>
      </div>
    </div>
  );
}
