import useFormValidation from "../../utils/useFormValidation";
import PopupWithForm from "../PopapWithForm/PopupWithForm";

export default function EditProfilePopup({isOpen, onClose}) {
const { values, errors, isInputValid, isValid, handleChange} = useFormValidation()
  return (
    <PopupWithForm
      name="profile-edit"
      title="Редактировать профиль"
      btnTitle="Сохранить"
      isOpen ={isOpen}
      onClose={onClose}
    >
      {" "}
      <fieldset className="popup__elements">
        <input
          className="popup__element"
          type="text"
          name="profilename"
          id="name"
          placeholder="Имя"
          minLength={2}
          maxLength={40}
          required=""
          onChange={handleChange}
        />
        <span className="popup__error popup__error_type_profilename">{errors.username}</span>
        <input
          className="popup__element"
          type="text"
          name="job"
          id="job"
          placeholder="Вид деятельности"
          minLength={2}
          maxLength={200}
          required=""
          onChange={handleChange}
        />
        <span className="popup__error popup__error_type_job">[[]]</span>
      </fieldset>
    </PopupWithForm>
  );
}
