import Header from "./Header/Header";
import Main from "./Main/Main";
import Footer from "./Footer/Footer";
import PopupWithForm from "./PopapWithForm/PopupWithForm";
import ImagePopup from "./ImagePopup/ImagePopup";
import { useCallback, useState, useEffect } from "react";
import currentUserContext from "../contexts/CurrentUserContext";
import api from "../utils/api";
import EditProfilePopup from "./EditProfilePopup/EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup/EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup/AddPlacePopup";

function App() {
  // Popup
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isImagePopupOpen, setIsImagePopup] = useState(false);
  const [selectedCard, setSelectedCard] = useState({});
  const [isDelPopupOpen, setDelPopupOpen] = useState(false);
  // Context
  const [currentUser, setCurrentUser] = useState({});
  // Card
  const [cards, setCards] = useState([]);
  // удаление карточки
  const [deleteCardId, setDeleteCardId] = useState("");

  // const [isLike, setIsLike] = useState(false);
  // const [count, setCount] = useState(likes.length)

  // useEffect(() => {
  //   setIsLike(likes.some(el => myId === el._id))
  // }, [likes, myId])

  const setCloseAllPopup = useCallback(() => {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsImagePopup(false);
    setDelPopupOpen(false);
  }, []);
  const closePopupByEsc = useCallback(
    (event) => {
      if (event.key === "Escape") setCloseAllPopup();
      document.removeEventListener("keydown", closePopupByEsc);
    },
    [setCloseAllPopup]
  );
  const CloseAllPopup = useCallback(() => {
    setCloseAllPopup();
    document.addEventListener("keydown", closePopupByEsc);
  }, [setCloseAllPopup, closePopupByEsc]);

  function setEventListenerByEsc() {
    document.addEventListener("keydown", closePopupByEsc);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
    setEventListenerByEsc();
  }
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
    setEventListenerByEsc();
  }
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
    setEventListenerByEsc();
  }
  function handleCardClick(card) {
    setSelectedCard(card);
    setIsImagePopup(true);
    setEventListenerByEsc();
  }
  function handleDelPopupClick(cardid) {
    setDeleteCardId(cardid);
    setDelPopupOpen(true);
    setEventListenerByEsc();
  }
  // я бы сделал отдельным компонентом, это было бы правильнее?
  function handleCardLike(card) {
    const isLiked = card.likes.some((i) => i._id === currentUser._id);

    if (!isLiked) {
      api.addLike(card._id).then((newCard) => {
        setCards((state) =>
          state.map((c) => (c._id === card._id ? newCard : c))
        );
      });
    } else {
      api.deleteLike(card._id).then((newCard) => {
        setCards((state) =>
          state.map((c) => (c._id === card._id ? newCard : c))
        );
      });
    }
  }

  setEventListenerByEsc();

  useEffect(() => {
    Promise.all([api.getInfo(), api.getCards()])

      .then(([resInfo, resCards]) => {
        setCurrentUser(resInfo);
        setCards(resCards);
      })
      .catch((error) => console.error(`Ошибка  ${error}`));
  }, []);

  function handleSubmit(e) {
    e.preventDefault();
    api
      .deleteCard(deleteCardId)
      .then(() => {
        setCards(
          cards.filter((el) => {
            return el._id !== deleteCardId;
          })
        );
        CloseAllPopup();
      })
      .catch((error) => console.error(`Ошибка  ${error}`));
  }

  function handleUpdateUser(dataUser, reset) {
    api
      .setUserInfo(dataUser)
      .then((el) => {
        setCurrentUser(el);
        CloseAllPopup();
        reset();
      })
      .catch((error) => console.error(`Ошибка  ${error}`));
  }
  function handleUpdateAvatar(dataUser, reset) {
    api
      .setUserAva(dataUser)
      .then((el) => {
        setCurrentUser(el);
        CloseAllPopup();
        reset();
      })
      .catch((error) => console.error(`Ошибка  ${error}`));
  }
  function handleAddPlaceSubmit(dataCard, reset) {
    api
      .addCard(dataCard)
      .then((el) => {
        setCards([el, ...cards]);
        CloseAllPopup();
        reset();
      })
      .catch((error) => console.error(`Ошибка  ${error}`));
  }
  return (
    <currentUserContext.Provider value={currentUser}>
      <div className="page__content">
        <Header />

        <Main
          oneEditProfile={handleEditProfileClick}
          onEditAvatar={handleEditAvatarClick}
          onAddPlace={handleAddPlaceClick}
          onClickCard={handleCardClick}
          onDelete={handleDelPopupClick}
          cards={cards}
          onCardLike={handleCardLike}
        />

        <Footer />

        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={CloseAllPopup}
          onUpdateUser={handleUpdateUser}
        ></EditProfilePopup>

        <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={CloseAllPopup}
          onUpdateAvatar={handleUpdateAvatar}
        />

        <AddPlacePopup
          isOpen={isAddPlacePopupOpen}
          onClose={CloseAllPopup}
          onAddPlace={handleAddPlaceSubmit}
        />

        <PopupWithForm
          name="delete"
          title="Вы уверены?"
          btnTitle="Да"
          isOpen={isDelPopupOpen}
          onClose={CloseAllPopup}
          onSubmit={handleSubmit}
        />
        <ImagePopup
          card={selectedCard}
          isOpen={isImagePopupOpen}
          onClose={CloseAllPopup}
        />
      </div>
    </currentUserContext.Provider>
  );
}

export default App;
