import Header from "./Header/Header";
import Main from "./Main/Main";
import Footer from "./Footer/Footer";
import PopupWithForm from "./PopapWithForm/PopupWithForm";
import ImagePopup from "./ImagePopup/ImagePopup";
import { useCallback, useState } from "react";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isImagePopupOpen, setIsImagePopup] = useState(false);
  const [selectedCard, setSelectedCard] = useState({});
  const [isDelPopupOpen, setDelPopupOpen] = useState(false);

  const setCloseAllPopup = useCallback(() => {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsImagePopup(false);
    setDelPopupOpen(false);
  }, []);
  const closePopupByEsc = useCallback(
    (event) => {
      if (event.key === "Escape") setCloseAllPopup();
      document.removeEventListener("keydown", closePopupByEsc);
    },
    [setCloseAllPopup]
  );
  coms

  function setEventListenerByEsc() {
    document.addEventListener("keydown", closePopupByEsc);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
    setEventListenerByEsc();
  }
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
    setEventListenerByEsc();
  }
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
    setEventListenerByEsc();
  }
  function handleCardClick(card) {
    setSelectedCard(card);
    setIsImagePopup(true);
    setEventListenerByEsc();
  }
  setEventListenerByEsc();
  function handleDelPopupClick() {
    setDelPopupOpen(true);
    setEventListenerByEsc();
  }

  function closePopupExitOverlay(event) {
    if (event.target === event.currentTarget) {
      setCloseAllPopup();
      document.removeEventListener("keydown", closePopupByEsc);
    }
  }

  return (
    <div className="page__content">
      <Header />

      <Main
        oneEditProfile={handleEditProfileClick}
        onEditAvatar={handleEditAvatarClick}
        onAddPlace={handleAddPlaceClick}
        onClickCard={handleCardClick}
        onDelete={handleDelPopupClick}
      />

      <Footer />

      <PopupWithForm
        name="profile-edit"
        title="Редактировать профиль"
        btnTitle="Сохранить"
        isOpen={isEditProfilePopupOpen}
        onClose={closePopupExitOverlay}
      >
        {" "}
        <fieldset className="popup__elements">
          <input
            className="popup__element"
            type="text"
            name="profilename"
            id="name"
            placeholder="Имя"
            minLength={2}
            maxLength={40}
            required=""
          />
          <span className="popup__error popup__error_type_profilename" />
          <input
            className="popup__element"
            type="text"
            name="job"
            id="job"
            placeholder="Вид деятельности"
            minLength={2}
            maxLength={200}
            required=""
          />
          <span className="popup__error popup__error_type_job" />
        </fieldset>
      </PopupWithForm>

      <PopupWithForm
        name="avatar-edit"
        title="Обновить аватар"
        btnTitle="Обновить"
        isOpen={isEditAvatarPopupOpen}
        onClose={closePopupExitOverlay}
      >
        <fieldset className="popup__elements">
          <input
            className="popup__element"
            type="url"
            name="avatar"
            id="ava"
            placeholder="Ссылка на картинку"
            required=""
          />
          <span className="popup__error popup__error_type_avatar" />
        </fieldset>
      </PopupWithForm>

      <PopupWithForm
        name="add-Card"
        title="Новое место"
        btnTitle="Добавить"
        isOpen={isAddPlacePopupOpen}
        onClose={closePopupExitOverlay}
      >
        <fieldset className="popup__elements">
          <input
            className="popup__element"
            type="text"
            name="title"
            id="title"
            placeholder="Название"
            minLength={2}
            maxLength={30}
            required=""
          />
          <span className="popup__error popup__error_type_title" />
          <input
            className="popup__element"
            type="url"
            name="link"
            id="link"
            placeholder="Ссылка на картинку"
            required=""
          />
          <span className="popup__error popup__error_type_link" />
        </fieldset>
      </PopupWithForm>

      <PopupWithForm
        name="delete"
        title="Вы уверены?"
        btnTitle="Да"
        isOpen={isDelPopupOpen}
        onClose={closePopupExitOverlay}
      />
      <ImagePopup
        card={selectedCard}
        isOpen={isImagePopupOpen}
        onClose={closePopupExitOverlay}
      />
    </div>
  );
}

export default App;
