export default function ImagePopup({card, isOpen, onClose}) {
  console.log(card)
  return (
    <div className={`popup popup_img-form ${isOpen && ' popup_opened'}`}>
      <div className="popup__gallery">
        <button
          className="popup__close"
          type="button" 
          title="Закрыть окно"
          aria-label="Закрыть"
          onClick={onClose}
        />
        <figure className="popup__figure-img">
          <img className="popup__image" src={card.link} alt={card.name} />
          <figcaption className="popup__figurecaption-img">{card.name}</figcaption>
        </figure>
      </div>
    </div>
  );
}
